SnippetCode.val:
using System;
using System.Data;
using System.Collections.Generic;
using Microsoft.Office.Interop.Excel;
using System.Text.RegularExpressions;
using System.Runtime.InteropServices;
using System.Windows.Forms;
using System.Globalization;
using System.Collections;
using System.Drawing;  

public static class Snippet
{
	public static void func(ref System.Data.DataTable tbl, ref System.Collections.Hashtable parameters, ref System.String judul, System.Boolean showChart, System.Boolean ChartByRow, System.Int16 ChartType, ref System.String reportNumberFormat)
	{		
		Microsoft.Office.Interop.Excel._Application excel = null;

		int rowcount = tbl.Rows.Count;
		int columncount = tbl.Columns.Count;
		DateTime jan1900 = new DateTime(1900, 1, 1);
		DateTime feb1900 = new DateTime(1900, 2, 28);

		// If there are columns and records
		if( rowcount>0 && columncount>0)
		{

			// Create Header array			
			var headerData = new object[columncount];
			int iCol = 0;
			foreach (DataColumn c in tbl.Columns)
			{
				headerData[iCol] = c.ColumnName;
				iCol++;
			}

			// Create data array out of the datatable (to improve performance) + Set the Display value instead of Item value
			var data = new object[rowcount, columncount];
			string tmpvalue;
			// string newvalue;
			for (var row = 1; row <= rowcount; row++)
			{
				for (var column = 1; column <= columncount; column++)
				{
					if (tbl.Columns[column-1].DataType == System.Type.GetType("System.Boolean"))
					{
						data[row - 1, column - 1] = tbl.Rows[row-1].ItemArray[column-1].ToString();						
					}
					else if (tbl.Columns[column-1].DataType == System.Type.GetType("System.DateTime"))
					{
						if ((tbl.Rows[row-1].ItemArray[column-1]).ToString()=="")
						{
							data[row - 1, column - 1] = "";
						}
						else
						{
							DateTime dt = ((System.DateTime)tbl.Rows[row-1].ItemArray[column-1]);
							// for dates between 1-jan-1900 and 28-feb-1900 excel adds one day 
							// so subtract one day before sending
							if (dt>=jan1900 && dt<=feb1900) 
							{
								dt = dt.AddDays(-1);
							}
							data[row - 1, column - 1] = dt.ToOADate();
						}
					}
					else if (tbl.Columns[column-1].DataType == System.Type.GetType("System.Double"))
					{
						// YRH: Fix for defect - 146283. Added if condition to check isDBNull.
						if (Convert.IsDBNull((tbl.Rows[row-1].ItemArray[column-1])))
						{
							data[row - 1, column - 1] = "";
						}
						else
							data[row - 1, column - 1] = ((System.Double)tbl.Rows[row-1].ItemArray[column-1]).ToString(CultureInfo.InvariantCulture);
					}
					else 
					{
						tmpvalue=tbl.Rows[row-1].ItemArray[column-1].ToString();
						data[row - 1, column - 1] = tmpvalue;
					}
				}
			}

			// Define the Excel variables
			//Microsoft.Office.Interop.Excel.Application excel = new Microsoft.Office.Interop.Excel.Application();

			if (excel==null)  //excel has value only in preview - to improve performance
				excel = new Microsoft.Office.Interop.Excel.Application();

			Microsoft.Office.Interop.Excel.Workbooks books = excel.Workbooks;
			Microsoft.Office.Interop.Excel.Workbook book = books.Add(Type.Missing);
			Microsoft.Office.Interop.Excel.Worksheet sheet = (Microsoft.Office.Interop.Excel.Worksheet)book.ActiveSheet;
			Microsoft.Office.Interop.Excel.ErrorCheckingOptions erroroptions = excel.ErrorCheckingOptions;

			// Set the column format to Text for string data (so that large numeric values in string var will appear properly
			// Set the column format to date according to the date format
			for (iCol = 1; iCol <= columncount; iCol++) 
			{
				if (tbl.Columns[iCol-1].DataType == System.Type.GetType("System.String"))
					((Microsoft.Office.Interop.Excel.Range)sheet.Columns[iCol]).NumberFormat = "@";
				else if (tbl.Columns[iCol-1].DataType == System.Type.GetType("System.DateTime"))
					((Microsoft.Office.Interop.Excel.Range)sheet.Columns[iCol]).NumberFormat = "m/d/yyyy";
				    else if (tbl.Columns[iCol-1].DataType == System.Type.GetType("System.Double"))
    				{
        			if(string.IsNullOrEmpty(reportNumberFormat))
        			{
					// With thousands separators and 2 decimal places
					((Microsoft.Office.Interop.Excel.Range)sheet.Columns[iCol]).NumberFormat = "#,##0.00";
						}
						else
						{
							// Format Number depends on parameter from Magic XPA
							((Microsoft.Office.Interop.Excel.Range)sheet.Columns[iCol]).NumberFormat = reportNumberFormat;
						}
					}


			}
			// Ignore the warning for numeric value in text format
			erroroptions.NumberAsText = false;

			//Set the first line of data (and data header)
			int StartAtRow = 2;  //change this value if you need to add a header before the table title and data
			if (showChart)
				StartAtRow = 21;

            Microsoft.Office.Interop.Excel.Range JudulTitleRange = sheet.get_Range((Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow, 1], (Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow, columncount]);
            JudulTitleRange.Merge(true);
            JudulTitleRange.Value2 = new String[1,1] { {judul} };
            JudulTitleRange.Cells.Font.Bold = true;
            JudulTitleRange.Cells.Font.Size = 16;
            JudulTitleRange.Cells.HorizontalAlignment = Microsoft.Office.Interop.Excel.XlHAlign.xlHAlignCenter;

            StartAtRow += 2;

			if (parameters != null && parameters.Count > 0)
            {
			    var dataParams = new object[rowcount, columncount];
                int idxParam = 0;

                foreach(DictionaryEntry item in parameters)
                {
                    dataParams[idxParam, 0] = item.Key; 
                    dataParams[idxParam, 1] = item.Value; 

                    idxParam += 1;
                }				

				Microsoft.Office.Interop.Excel.Range filterSectionRange = sheet.get_Range((Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow, 1], (Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow + parameters.Count, 2]);

				Microsoft.Office.Interop.Excel.Range filterTitleRange = sheet.get_Range((Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow, 1], (Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow, 2]);
				filterTitleRange.Merge(true);
				filterTitleRange.Value2 = new String[1,1] { {"Filters"} };

				filterTitleRange.Cells.Font.Bold = true;
				filterTitleRange.Cells.Font.Size = 14;
				filterTitleRange.Cells.HorizontalAlignment = Microsoft.Office.Interop.Excel.XlHAlign.xlHAlignCenter;

				StartAtRow += 1;

                // Set the header row format to be string  case the header is a numeric value) and write the header array to Excel
                Microsoft.Office.Interop.Excel.Range startCellParams = (Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow, 1];
                Microsoft.Office.Interop.Excel.Range endCellParams = (Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow + parameters.Count - 1, 2];
                Microsoft.Office.Interop.Excel.Range writeRangeParams = sheet.get_Range(startCellParams, endCellParams);

				writeRangeParams.NumberFormat = "@";
                writeRangeParams.Value2 = dataParams;

				writeRangeParams.Cells.Font.Bold = true;
				// writeRangeParams.Cells.BorderAround(Microsoft.Office.Interop.Excel.XlLineStyle.xlContinuous, Microsoft.Office.Interop.Excel.XlBorderWeight.xlThin);

				filterSectionRange.Borders.Weight = Microsoft.Office.Interop.Excel.XlBorderWeight.xlThin;
				filterSectionRange.Borders.LineStyle = Microsoft.Office.Interop.Excel.XlLineStyle.xlContinuous;
				//filterSectionRange.Borders.Color = ColorTranslator.ToOle(Color.FromArgb(91, 155, 213));
				filterSectionRange.Borders.Color = ColorTranslator.ToOle(Color.Black);

				StartAtRow += parameters.Count + 2; // Add space 2 rows
            }

			// Set the header row format to be string  case the header is a numeric value) and write the header array to Excel
			Microsoft.Office.Interop.Excel.Range startCell = (Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow, 1];
            Microsoft.Office.Interop.Excel.Range endCell = (Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow, columncount];
            Microsoft.Office.Interop.Excel.Range writeRange = sheet.get_Range(startCell, endCell);
			writeRange.NumberFormat = "@";
			writeRange.Value2 = headerData;

			//	Write the data array to Excel
			startCell = (Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow+1, 1];
            endCell = (Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow+1+rowcount-1, columncount];
            writeRange = sheet.get_Range(startCell, endCell);
			writeRange.Value2 = data;

			// Set Table style
			startCell = (Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow, 1];
            endCell = (Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow+1+rowcount-1, columncount];
            writeRange = sheet.get_Range(startCell, endCell);
			sheet.ListObjects.AddEx(XlListObjectSourceType.xlSrcRange, writeRange, Type.Missing, Microsoft.Office.Interop.Excel.XlYesNoGuess.xlYes, Type.Missing).Name = "MyTableStyle";
			//sheet.ListObjects.get_Item("MyTableStyle").TableStyle = "TableStyleLight9";
			sheet.ListObjects.get_Item("MyTableStyle").TableStyle = "TableStyleLight8";


			// Set width to auto fit
			writeRange.EntireColumn.AutoFit();
			// Increase the column width to default size if its smaller
            double currentWidth = 0;
			for (iCol = 1; iCol <= columncount; iCol++) 
			{
				currentWidth = (double)((Microsoft.Office.Interop.Excel.Range)sheet.Columns[iCol]).ColumnWidth;
				if (currentWidth < 10)
					((Microsoft.Office.Interop.Excel.Range)sheet.Columns[iCol]).ColumnWidth = 10;
			}

			// Add the chart
			if (showChart)
			{
				startCell = (Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow, 1];
            	endCell = (Microsoft.Office.Interop.Excel.Range)sheet.Cells[StartAtRow+1+rowcount-1, columncount];
				int endRow = StartAtRow+rowcount;
            	// writeRange = (Microsoft.Office.Interop.Excel.Range)sheet.Range["A27:A"+StartAtRow+rowcount+";C27:C"+StartAtRow+rowcount+";D27:D"+StartAtRow+rowcount];
            	MessageBox.Show("Formula: " + "A27:A"+(StartAtRow+rowcount)+";C27:C"+endRow+";D27:D"+endRow);
				writeRange = (Microsoft.Office.Interop.Excel.Range)sheet.Range["A27:A119;C27:C119;D27:D119"];
            	//writeRange = sheet.get_Range(startCell, endCell);
				Microsoft.Office.Interop.Excel.ChartObjects xlCharts = (Microsoft.Office.Interop.Excel.ChartObjects)sheet.ChartObjects(Type.Missing);
				Microsoft.Office.Interop.Excel.ChartObject myChart = (Microsoft.Office.Interop.Excel.ChartObject)xlCharts.Add(15, 15, 400, 250);
				Microsoft.Office.Interop.Excel.Chart chartPage = myChart.Chart;
				// The following two lines can set the chart to a dedicated page
				//Microsoft.Office.Interop.Excel.Sheets charts = book.Charts;
				//Microsoft.Office.Interop.Excel.Chart chartPage = (Chart)charts.Add();
				chartPage.HasTitle = true;
				//chartPage.ChartTitle.Text = "Title";
				chartPage.SetSourceData(writeRange, (ChartByRow) ? Microsoft.Office.Interop.Excel.XlRowCol.xlRows :Microsoft.Office.Interop.Excel.XlRowCol.xlColumns);
				Microsoft.Office.Interop.Excel.XlChartType charttypeEnum = new Microsoft.Office.Interop.Excel.XlChartType();
				chartPage.ChartType = (Microsoft.Office.Interop.Excel.XlChartType)System.Enum.GetValues(charttypeEnum.GetType()).GetValue(ChartType); 
			}

			bool PreviewOnly = false;

			// Show Excel and bring it to the foreground (if not preview)
			if (!PreviewOnly)
			{
				string caption = excel.Caption;
				IntPtr handler = FindWindow(null, caption);
				SetForegroundWindow(handler);

				excel.Visible = true;
				// excel.WindowState=XlWindowState.xlMinimized;
				// excel.WindowState=XlWindowState.xlNormal;
			}

			// Release Excel objects
			Marshal.ReleaseComObject(sheet);
			Marshal.ReleaseComObject(book);
			Marshal.ReleaseComObject(books);
			Marshal.ReleaseComObject(erroroptions);
			if (!PreviewOnly)
				Marshal.ReleaseComObject(excel);
			GC.Collect();
		}	




	}

	[DllImport("user32.dll")]
	[return: MarshalAs(UnmanagedType.Bool)]
	static extern bool SetForegroundWindow(IntPtr hWnd);

	[DllImport("user32.dll", SetLastError = true)]
	static extern IntPtr FindWindow(string lpClassName, string lpWindowName); 

}
