using System;
using System.Reflection;
using Newtonsoft.Json.Linq;

public static class Snippet
{
	public static System.String GetStringJson(Newtonsoft.Json.Linq.JObject jsonData, System.String propName)
	{		
		try {
			//return jsonData.GetValue(propName).Value<string>();

			return jsonData.SelectToken(propName).Value<string>();
		}
		catch(Exception)
		{
			return "";
		}
	}

}

using System;
using System.Diagnostics;
using System.IO;
using Microsoft.Win32;
using System.Windows.Forms;

public static class Snippet
{
	public static System.Boolean openUrlWithChrome(System.String urlToOpen, System.String appDataPath, System.String browserFolderPathName)
	{
		try {
			// MessageBox.Show("folder path name: " + browserFolderPathName);

			if (browserFolderPathName.Trim() == "")
				browserFolderPathName = "LevelupBrowser";

			var chromeDataFolderPath = Path.Combine(appDataPath, browserFolderPathName.Trim());
			var folderExists = Directory.Exists(chromeDataFolderPath);

			if(!folderExists)
				Directory.CreateDirectory(chromeDataFolderPath);

			const string userRoot = "HKEY_LOCAL_MACHINE";
			const string subkey = @"SOFTWARE\Microsoft\Windows\CurrentVersion\App Paths\chrome.exe";
			const string keyName = userRoot + "\\" + subkey;

			string chromeFolderPath = (string) Registry.GetValue(keyName, "Path", "");
			string fullChromeExePath = chromeFolderPath + "\\chrome.exe";

			int screenWidth = System.Windows.Forms.Screen.PrimaryScreen.Bounds.Width;
			int screenHeight = System.Windows.Forms.Screen.PrimaryScreen.Bounds.Height;

			// MessageBox.Show("finished get screen res. h: " + screenHeight + " w: " + screenWidth);

			int chromeWidth = Convert.ToInt32(0.8 * screenWidth); // 80% dari screen width;
			int chromeHeight = Convert.ToInt32(0.8 * screenHeight); // 80% dari screen height;

			// MessageBox.Show("finished get screen chrome. h: " + chromeHeight + " w: " + chromeWidth);

			int centerWidth = (screenWidth - chromeWidth) / 2;
			int centerHeight = (screenHeight - chromeHeight) / 2;

			// MessageBox.Show("finished get screen center. h: " + centerHeight + " w: " + centerWidth);

			// MessageBox.Show(string.Format("screen w: {0} , h: {1} ; center w: {2} , center h {3}", screenWidth, screenHeight, centerWidth, centerHeight));
			string profilePath = chromeDataFolderPath;

			string chromeCommand = string.Format(
				"--new-window --user-data-dir=\"{0}\" --window-size=\"{1},{2}\" --window-position=\"{3},{4}\" --incognito --app=\"{5}\" ",
				profilePath,
				chromeWidth, chromeHeight,
				centerWidth, centerHeight,
				urlToOpen
			);

			//string chromeCommand = " --new-window --window-size=\"1024,720\" \"" + urlToOpen + "\"";
			// MessageBox.Show("Chrome Command: " + chromeCommand);

			Process p = Process.Start(fullChromeExePath, chromeCommand);
			p.WaitForExit();

			if (p.HasExited == false) 
			{
				if (p.Responding) 
				{
					p.CloseMainWindow();
				}
				else 
				{
					p.Kill();
				}
			}

			// MessageBox.Show("Has Exit: " + (p.HasExited ? "Yes" : "No"));

			return p.HasExited;
		}
		catch(Exception e)
		{
			MessageBox.Show("Error display payment url : " + e.Message);

			return true;
		}
	}

}

using System;
using System.Diagnostics;
using Microsoft.Win32;
using System.Windows.Forms;

public static class Snippet
{
	public static System.Boolean openUrlWithChrome(System.String urlToOpen)
	{
		try {
			const string userRoot = "HKEY_LOCAL_MACHINE";
			const string subkey = @"SOFTWARE\Microsoft\Windows\CurrentVersion\App Paths\chrome.exe";
			const string keyName = userRoot + "\\" + subkey;

			string chromeFolderPath = (string) Registry.GetValue(keyName, "Path", "");
			string fullChromeExePath = chromeFolderPath + "\\chrome.exe";

			int screenWidth = System.Windows.Forms.Screen.PrimaryScreen.Bounds.Width;
			int screenHeight = System.Windows.Forms.Screen.PrimaryScreen.Bounds.Height;

			// MessageBox.Show("finished get screen res. h: " + screenHeight + " w: " + screenWidth);

			int chromeWidth = Convert.ToInt32(0.8 * screenWidth); // 80% dari screen width;
			int chromeHeight = Convert.ToInt32(0.8 * screenHeight); // 80% dari screen height;

			// MessageBox.Show("finished get screen chrome. h: " + chromeHeight + " w: " + chromeWidth);

			int centerWidth = (screenWidth - chromeWidth) / 2;
			int centerHeight = (screenHeight - chromeHeight) / 2;

			// MessageBox.Show("finished get screen center. h: " + centerHeight + " w: " + centerWidth);

			// MessageBox.Show(string.Format("screen w: {0} , h: {1} ; center w: {2} , center h {3}", screenWidth, screenHeight, centerWidth, centerHeight));

			string chromeCommand = string.Format(
				"--new-window --window-size=\"{0},{1}\" --window-position=\"{2},{3}\" --incognito --app=\"{4}\" ",
				chromeWidth, chromeHeight,
				centerWidth, centerHeight,
				urlToOpen
			);

			//string chromeCommand = " --new-window --window-size=\"1024,720\" \"" + urlToOpen + "\"";
			// MessageBox.Show("Chrome Command: " + chromeCommand);

			Process p = Process.Start(fullChromeExePath, chromeCommand);
			p.WaitForExit();

			if (p.HasExited == false) 
			{
				if (p.Responding) 
				{
					p.CloseMainWindow();
				}
				else 
				{
					p.Kill();
				}
			}

			// MessageBox.Show("Has Exit: " + (p.HasExited ? "Yes" : "No"));

			return p.HasExited;
		}
		catch(Exception e)
		{
			MessageBox.Show("Error display payment url : " + e.Message);

			return true;
		}
	}

}

using System;
using System.Security.Cryptography;
using System.Text;
using System.Windows.Forms;

public static class Snippet
{
	public static System.Boolean verifiySignatureKey(System.String signatureKey, System.String orderId, System.String statusCode, System.String grossAmount, System.String serverKey)
	{	
		var data = orderId.Trim() + statusCode.Trim() + grossAmount.Trim() + serverKey.Trim();

		// MessageBox.Show("concat data: " + data);

		bool isVerified = false;

		using (SHA512 shaM = new SHA512Managed())
		{
			var stringBytes = Encoding.UTF8.GetBytes(data);
			var hashBytes = shaM.ComputeHash(stringBytes);
			// string hash = Encoding.UTF8.GetString().ToString();
			// string hash = BitConverter.ToString(hashBytes).Replace("-", "");
			string hash = BitConverter.ToString(hashBytes).Replace("-", "").ToLowerInvariant();

			if (hash == null)
				return false;

			// MessageBox.Show("passed Hash: " + signatureKey);
			// MessageBox.Show("verify Hash: " + hash);

			isVerified = (hash == signatureKey);
		}

		return isVerified;
	}

}

using System;
using System.Reflection;
using System.Dynamic;
using System.Linq;
using System.Collections.Generic;
using System.Windows.Forms;
using Newtonsoft.Json;
using Newtonsoft.Json.Linq;


public static class Snippet
{
	public static System.String createBodyPayload(System.String orderId, System.String grossAmount, System.String allowedPayments)
	{
		try {
			// Transaction Details
			dynamic transactionDetails = new JObject();
			transactionDetails["order_id"] = orderId.Trim();
			transactionDetails["gross_amount"] = grossAmount;

			// Allowed Payments
			List<string> enabledPayments = new List<string>();

			if(!string.IsNullOrEmpty(allowedPayments)) 
				enabledPayments = allowedPayments.Split(',').ToList();

			// Final Payload
			dynamic payload = new JObject();

			payload["transaction_details"] = transactionDetails;

			if(enabledPayments.Count > 0)
				payload["enabled_payments"] = JArray.FromObject(enabledPayments);

			var jsonPayload = JsonConvert.SerializeObject(payload);

			return jsonPayload;

		} catch(Exception e) {
			MessageBox.Show(e.Message);

			return "";
		}
	}

}
